import React, {useState,useEffect } from 'react';
import axios  from 'axios';

export default function Form1({sendMessage}) {
  
const initialstate = {_id:"",name:'raju',age:0,email:'',isEdit:false}
const [formdata, setData]     = useState(initialstate);
//function that handle post request
const create = () => {
  const{_id,name,age,email,isEdit} = formdata
   axios.post('http://localhost:5000/info',{name,age,email}).then((res) =>{
     console.log(res.data)
   }).catch((err) => {console.error(err)})
 }
  /*useEffect(() => {
    if(!data.isEdit){
      read();
    }
    else if(data.isEdit){
      update();
      setdata({...data,isEdit:false})
    }
  },[data.isEdit]);/*
 /* 
  useEffect(() => {
   } ,[data])*/
 
const getdata = (e) => {
 let val = e.target.value;
 let nm = e.target.name;
return setData({...formdata,[nm]:val})
 }

 const handleSubmit = (e) =>{
 e.preventDefault();
 create();
  
 }

  return (
  <form onSubmit={handleSubmit}>
    <div className="mb-3">
      <label for="" className="form-label">Name</label>
      <input type="text" className="form-control" name='name' onChange={getdata}/>
      
    </div>
    <div className="mb-3">
    <label htmlFor="" className="form-label">age</label>
      <input type="number" className="form-control"  name='age' onChange={getdata}/>
      
    </div>
    <div className="mb-3">
    <label htmlFor="" className="form-label">Email</label>
      <input type="email" className="form-control" name='email'onChange={getdata}/>
      
    </div>
    
    <button type="submit" className="btn btn-primary" >Submit</button>
  </form>
   
  )
}
