import React from 'react'

export default function Table1(data,setData,sendmessage) {
  const handleclick = () =>{
    setData({...data,isEdit:true});
  }
  const handleSubmit = () => {
    sendmessage(data);
    setData({...data,isEdit:false})
  }
   
  return (
    <table className="table table-hover">
    <thead>
    <tr>
        <th>S.no</th>
        <th>Name</th>
        <th>Age</th>
        <th>Email</th>
        <th>Edit</th>
        <th>Remove</th>
    </tr>
    </thead>
    <tbody>
    {data.map((d)=><tr key={data._id}><td>{0}</td>
      <td>{data.name}</td>
      <td>{data.age}</td>
      <td>{data.email}</td>
      <td>{data.isEdit ?<button type="button" className="btn btn-primary" onClick={handleclick}>update</button>:<button type="button" className="btn btn-primary" onClick={handleSubmit}>Submit</button>}</td>
      <td><button type="button" className="btn btn-danger">delete</button></td>
      </tr>)}
      
    </tbody>
  </table>
  )
}
