import React, { useState } from 'react';
import axios from 'axios';

export default function Table1(props) {
  const{data,setdata,sendmessage,isEdit} = props
  const [tb,settb] = useState(data)
  // function that update
  const update = () => {
    const{_id,name,age,email,isEdit} = data;
    axios.patch(`http://localhost:5000/info/update/${_id}`)
  };
  const handleclick = () =>{
   update();
  }
  const handleSubmit = () => {
    sendmessage()
  }
   
  return (
    <table className="table table-hover">
    <thead>
    <tr>
        <th>S.no</th>
        <th>Name</th>
        <th>Age</th>
        <th>Email</th>
        <th>Edit</th>
        <th>Remove</th>
    </tr>
    </thead>
    <tbody>
    {data.map((val) => <tr key={val._id}>
      <td></td>
      <td>{val.name}</td>
      <td>{val.age}</td>
      <td>{val.email}</td>
      <td>{!isEdit ?<button type="button" className="btn btn-primary" onClick={handleclick}>update</button>:<button type="button" className="btn btn-primary" onClick={handleSubmit}>Submit</button>}</td>
      <td><button type="button" className="btn btn-danger">delete</button></td>
      </tr>)}
    </tbody>
  </table>
  )
}
