//import './App.css';
import axios from "axios";
import { useEffect, useState } from "react";
import Form1 from "./components/form1";
import Table1 from "./components/table1";


function App() {
  // for receiving data from child components
const [data,setdata] = useState({_id:"",name:'',age:0,email:'',isEdit:false});
const [tableData, setTable]=useState([]);
const [props, setProps] = useState([]);
const sendMessage = (data1) =>{
     setProps(data1);
     setdata(props[0]);
  } ;
  //function that handle post request
const create = (data) => {
   const {name, age,email} = data
   axios.post('http://localhost:5000/info',{name,age,email}).then((res) =>{
     console.log(res.data)
   }).catch((err) => {console.error(err)})
 }

  //fuction that retrive data from db
  const read = () =>{
    axios.get('http://localhost:5000/info').then((res) =>{
      setTable(res.data) 
    }).catch((err) => {console.error(err)})
  }

  useEffect(() =>{
    read()
  },[]);



  return (
    <div className="container text-center">
    <div className="row">
      <div className="col">
        <Form1 myData={create}/>
      </div>
      <div className="col">
        <Table1 data={tableData}setdata={setdata}sendmessage={sendMessage} isEdit={data.isEdit}/>
      </div>

    </div>
  </div>
  );
}

export default App;
