//import './App.css';
import axios from "axios";
import { useEffect, useState } from "react";
import Form1 from "./components/form1";
import Table1 from "./components/table1";


function App() {
  // for receiving data from child components
  const [data,setdata] = useState('')
  const [props, setProps] = useState([])
  const sendMessage = (data) =>{
     setProps(data);
     setdata(props[0]);
  } ;
  //function that handle post request
  const create = () => {
    axios.post('http://localhost:5000/info',data).then((res) =>{
      console.log(res.data)
    }).catch((err) => {console.error(err)})
  }
  //fuction that retrive data from db
  const read = () =>{
    axios.get('http://localhost:5000/info',data).then((res) =>{
      setdata(res.data)
    }).catch((err) => {console.error(err)})
  }

  useEffect(() => {
    read();
  },[data]);
 /* 
