   } ,[data])*/
  return (
    <div className="container text-center">
    <div className="row">
      <div className="col">
        <Form1 sendMessage={sendMessage}/>
      </div>
      <div className="col">
        <Table1 data={data}setData = {props[1]} sendmessage={sendMessage}/>
      </div>

    </div>
  </div>
  );
}

export default App;
