import axios from "axios";
import { useEffect, useState } from "react";
import Form1 from "./components/form1";
import Table1 from "./components/table1";


function App() {
  // for receiving data from child components
  const [data,setdata] = useState({_id:"",name:'',age:0,email:'',isEdit:false});
const [tableData, setTable]=useState([{...data}]);
  const [props, setProps] = useState([]);
  const sendMessage = (data) =>{
     setProps(data);
     setdata(props[0]);
  } ;
  //function that handle post request
  const create = () => {
   const{_id,name,age,email,isEdit} = data
    axios.post('http://localhost:5000/info',{name,age,email}).then((res) =>{
      console.log(res.data)
    }).catch((err) => {console.error(err)})
  }
  //fuction that retrive data from db
  const read = () =>{
    axios.get('http://localhost:5000/info').then((res) =>{
      setTable(res.data)
    }).catch((err) => {console.error(err)})
  }
  //function that update
  const update = () => {
    const{_id,name,age,email,isEdit} = data;
    axios.patch(`http://localhost:5000/info/update/${_id}`,)

  };
  useEffect(() =>{
    read()
  },[]);
  useEffect(() =>{
    create();
  },[]);

  useEffect(() => {
    if(!data.isEdit){
      read();
    }
    else if(data.isEdit){
      update();
      setdata({...data,isEdit:false})
    }
  },[data.isEdit]);
