import React, { useState } from 'react'

export default function Form1() {
const [data, setData]     = useState({name:'',age:0,email:''});
 getData = (e) =>{
    let val = e.target.value;
    let nm = e.target.name;
    let pr = {[nm]:val}
    setData(...data,...pr)
}

  return (
  <form>
    <div className="mb-3">
      <label for="" className="form-label">Name</label>
      <input type="text" className="form-control" name='name' onChange={getData}/>
      
    </div>
    <div className="mb-3">
    <label for="" className="form-label">age</label>
      <input type="number" className="form-control"  name='age' onChange={getData}/>
      
    </div>
    <div className="mb-3">
    <label for="" className="form-label">Email</label>
      <input type="email" className="form-control" name='email'onChange={getData}/>
      
    </div>
    
    <button type="submit" className="btn btn-primary" >Submit</button>
  </form>
   
  )
}
